class RpgCube{constructor(target,type=6,num=2){this.diceset={high:[4,5,6],cheat_l:[1,1,1,2,3,4,5,6],cheat_h:[1,2,3,4,5,6,6,6]},this.type=type,this.target=target,this.num=num,this.current=[1,1],this.rotateCount=1}getHtml(){let html="";for(let i=0;i<this.num;i++)Array.isArray(this.type)||(this.current[i]=Util.random(1,this.type)),html+=`<img src="${ROOT}${COMMON}cube${this.current[i]}.png" class="dice" />`;return html}rotateOne(){this.rotateCount++,$(target).html(this.getHtml()),rotate_count>20||setTimeout(this.rotateOne,50)}rotate(){SeAudio.play("dice",!0),rotate_count=1,this.rotateOne()}getRoll(key){switch(this.num){case 2:if("L"===key)return this.current[0];if("R"===key)return this.current[1];break;case 3:if("L"===key)return this.current[0];if("M"===key)return this.current[1];if("R"===key)return this.current[2];break;case 4:if("L"===key)return this.current[0];if("LM"===key)return this.current[1];if("RM"===key)return this.current[2];if("R"===key)return this.current[3];break;case 5:if("L"===key)return this.current[0];if("LM"===key)return this.current[1];if("M"===key)return this.current[2];if("LR"===key)return this.current[3];if("R"===key)return this.current[4]}return 0}}